                Films.Add(new Film { Name = "12 лет рабства", Genre = "Драма/Исторический/Биография", Year = "2013", Director = "Стив МакКуин", Story = "Соединенные Штаты, первая половина 19 века. Соломон Нортал – свободный негр. Он работает и зарабатывает неплохие деньги, у него есть прекрасная жена и дети, которых он очень любит и о которых заботится. Также Соломон очень красиво играет на скрипке, он хорошо образован и довольно приятный человек.\n Но однажды все кардинально изменилось.Когда весьма солидный человек предложил ему играть на концертах в другом городе, Соломон  стал жертвой заговора. Этот человек подпоил Соломона, а потом продал в рабство.", Photo = "/Posters/12_лет_рабства.jpg" });
