                Films.Add(new Film { Name = "Золотые руки", Genre = "Драма/Биография", Year = "2009", Director = "Томас Картер", Story = "Сюжет Бен Карсон рос без отца среди бедности и предрассудков, у него были ужасные оценки и вспыльчивый характер. Но мать, побуждая его использовать возможности, которых у неё никогда не было, способствовала развитию его воображения, интеллекта и, что самое главное, веры в таланты, заложенные в него Богом.", Photo = "/Posters/золотые_руки.jpg" });
                Films.Add(new Film { Name = "Лекарь: Ученик Авиценны ", Genre = "Драма/Романтика", Year = "2013", Director = "Филипп Штёльцль", Story = "Англия, XI век. Мать десятилетнего Роба Коула умирает в страшных мучениях: никто не знает ни природы её болезни, ни лекарства против неё.\nОставшись в одиночестве, он напрашивается в попутчики к странствующему знахарю Барберу. Взяв мальчика под свою опеку, он обучает его всем полученным знаниям и передает ему свой опыт. Но спустя некоторое время Роберт понимает, что против многих болезней они просто бессильны ", Photo = "/Posters/лекарь_ученик_авицены.jpg" });



